import{getPokemon}from"./get-pokemon-min.js";let imgPokemon=document.querySelector("#img_pokemon"),namePokemon=document.querySelector("#id_pokemon"),btnPokemon=document.querySelector("#btn_pokemon"),inputPokemon=document.querySelector("#input_search");const searchPokemon=async e=>{try{const n=await getPokemon(e);setTimeout(()=>{Swal.fire({position:"top-center",icon:"success",title:"Pokémon encontrado con éxito",backdrop:"rgb(9 79 167 / 40%)",showConfirmButton:!1,timer:1500}),imgPokemon.src=n.sprites.front_default,namePokemon.textContent=n.species.name},1e3),setTimeout(()=>{Swal.fire({html:createHtml(n),showConfirmButton:!1,showCloseButton:!0,background:"#ffc107",backdrop:"rgb(9 79 167 / 40%)"})},2500)}catch(e){Swal.fire({title:"Error!",text:`No existe el id o nombre: ${inputPokemon.value}`,icon:"error",backdrop:"rgba( 208, 2, 27, 0.15 )",confirmButtonText:"Continuar"}),console.clear(),clearInput(inputPokemon)}},createHtml=e=>{return`\n        <div class="box-img-modal">\n            <img src="${e.sprites.front_default}" alt="pokemon">\n        </div>\n\n        <section class="box-modal">\n\n        <h1 id="modal_pokemon_name">${e.species.name}</h1>\n        \n        <div class="container">\n        <div class="row align-items-center">\n\n            <div class="col-6">\n                <h4 class="text-left mt-3 class_decoration">Ataque</h4>\n            </div>\n\n            <div class="col">\n                <h4 id="id_abilities" class="text-center mt-3 class_decoration">${e.stats[1].base_stat}</h4>\n            </div>\n\n            <div class="col-6 mt-4">\n                <h4 class="text-left class_decoration">Defensa</h4> <br>\n            </div>\n\n            <div class="col mt-4">\n                <h4 class="text-center class_decoration">${e.stats[2].base_stat}</h4> <br>\n            </div> \n        </div>\n\n        </div>\n        \n    </section>`},searchInput=e=>{""===e.value?(Swal.fire({title:"Error!",text:"Algo salió mal, intentelo de nuevo",icon:"error",backdrop:"rgba( 208, 2, 27, 0.15 )",confirmButtonText:"Continuar"}),e.value=""):searchPokemon(e.value)},clearInput=e=>{e.value=""},init=()=>{btnPokemon.addEventListener("click",function(){var e;""===(e=inputPokemon).value?(Swal.fire({title:"Error!",text:"Algo salió mal, intentelo de nuevo",icon:"error",backdrop:"rgba( 208, 2, 27, 0.15 )",confirmButtonText:"Continuar"}),e.value=""):searchPokemon(e.value.toLowerCase())})};export{init};